import { Component } from 'react';
import './Portfolio.css';

class Portfolio extends Component {
  constructor(props) {
    super(props);
    this.state = {
      projects: [
        {
          project_image: "/E-commerce.jpg",
          title: "E-commerce Website",
          description: "A full-stack e-commerce platform built with React and Node.js",
          technologies: ["React", "Node.js", "MongoDB", "CSS3"],
        },
        {
          project_image: "",
          title: "Task Management App",
          description: "A collaborative task management application with real-time updates",
          technologies: ["React", "Socket.io", "Express", "PostgreSQL"],
        },
      ]
    };
  }

  render() {
    return (
      <div className="portfolio-section">
        <h3 className="about-title">My Portfolio</h3>
        <div className="portfolio-content">


          {this.state.projects.map((project, index) =>
            <div className="portfolio-block">

              <div className="project-image"> 
                { project.project_image.length > 0 ? <img src="{project.project_image}" />
                : <span> Project Image </span>
                }
              </div>

              <h4 className="item-title">{project.title}</h4>
              <p className="item-subtitle">{project.description}</p>
              <div className="item-details">
                {project.technologies.map(technology =>
                  <span className="item-technology">{technology}</span>
                )}
              </div>
              <div className="buttons">
                <a href="#" className="button live-demo"> Live Demo </a>
                <a href="#" className="button view-code"> View Code </a>
              </div>
            </div>
          )}

        </div>
      </div>
    );
  }
}

export default Portfolio;
